import React, { useEffect, useState } from "react";
import axios from "axios";
import Maps from "../components/Maps";
const AddExperience = (props) => {
  const [rating, setRating] = useState(0);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [searchCoordinates, setSearchCoordinates] = useState([]);
  const [location, setLocation] = useState([]);
  useEffect(() => {
    loactionSearchHandler();
  }, [search]);
  const loactionSearchHandler = async () => {
    if (search === "") {
      setSearchResults([]);
    }
    await axios
      .get(
        `https://api.geoapify.com/v1/geocode/autocomplete?text=${search}&apiKey=00c103485e70472888f4cd0ba88428e7`
      )
      .then((response) => {
        //setSearchResults(response.data);
        var temp = response.data;
        //console.log(response.data["features"][0]["properties"]["city"]);
        temp.features.forEach((feature, index) => {
          setSearchResults((prevState) => {
            return [...prevState, feature.properties.formatted];
          });
        });

        temp.features.forEach((feature, index) => {
          console.log(feature);
        });
      });
  };
  const submitHandler = (event) => {
    event.preventDefault();
  };
  return (
    <>
      <form onSubmit={submitHandler} className="form">
        <label htmlFor="title">Title</label>
        <input type="text" className="form-control" name="title" />
        <label htmlFor="description">Description</label>
        <input type="text" className="form-control" name="description" />
        <label htmlFor="author">Author Name</label>
        <input type="text" className="form-control" name="author" />
        <label htmlFor="location">Enter Location</label>
        <input
          type="text"
          className="form-control"
          name="location"
          value={search}
          onChange={(event) => setSearch(event.target.value)}
        />
        <div>
          <ul>
            {searchResults.map((result, index) => {
              return (
                <li
                  onClick={() => {
                    console.log(result);
                  }}
                >
                  {result}
                </li>
              );
            })}
          </ul>
        </div>
        <div>
          <label>Rating: </label>
          <button className="btn" onClick={() => setRating(rating + 1)}>
            +
          </button>
          {rating}
          <button className="btn" onClick={() => setRating(rating - 1)}>
            -
          </button>
        </div>

        <button className="btn btn-dark">Add</button>
      </form>
      <Maps location={location} />
    </>
  );
};

export default AddExperience;
