import React, { useEffect, useState, useRef } from "react";
import "@tomtom-international/web-sdk-maps/dist/maps.css";
import * as tt from "@tomtom-international/web-sdk-maps";
import axios from "axios";

const HomePage = (props) => {
  const mapElement = useRef();
  const [mapZoom, setMapZoom] = useState(13);
  const [map, setMap] = useState({});
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const searchHandler = async () => {
    if (search === "") {
      setSearchResults([]);
    }
    await axios
      .get(
        `https://api.tomtom.com/search/2/search/${search}.json?key=66AS5Y04A9wZlAR4jt93xH6dCVAwLzUr&language=en-US`
      )
      .then((response) => {
        response.data.results.map((res) => {
          return setSearchResults((prevState) => {
            return [...prevState.slice(0, 5), res.address.freeformAddress];
          });
        });
      });
  };

  useEffect(() => {
    let map = tt.map({
      key: "66AS5Y04A9wZlAR4jt93xH6dCVAwLzUr",
      stylesVisibility: {
        trafficIncidents: true,
        trafficFlow: true,
      },
      container: mapElement.current,
      center: [10.33, 45.432],
      zoom: mapZoom,
    });
    // var marker = new tt.Marker()
    //   .setLngLat([props.location[0], props.location[1]])
    //   .addTo(map);

    setMap(map);
    return () => map.remove();
  }, []);
  useEffect(() => {
    searchHandler();
  }, [search]);
  return (
    <>
      <h1 className="text-center">Home</h1>
      <div className="container">
        <div className="row">
          <div className="col-8">
            <div id="main-map" ref={mapElement} className="mapDiv" />
          </div>
          <div className="col-4">
            <form>
              <input
                type="text"
                placeholder="Enter Your Location"
                className="form-control"
                onChange={(e) => {
                  setSearch(e.target.value);
                }}
              />
            </form>
            <div>
              {searchResults.slice(0, 8).map((result, index) => {
                return <h4>{result}</h4>;
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default HomePage;
